'''Inspiration: https://www.youtube.com/watch?v=pMIwu5FwJ78CSS Courtesy: ChatGPT'''from flask import Flask, render_template, requestimport pickleimport numpy as npapp = Flask(__name__)@app.route("/")@app.route("/predictor", methods=["GET", "POST"])def index():    amt = None        if request.method == "POST":        num_rooms = request.form['num_rooms']        num_people = request.form['num_people']        house_area = request.form['house_area']        is_ac = request.form['is_ac']        is_tv = request.form['is_tv']        is_flat = request.form['is_flat']        avg_monthly_income = request.form['avg_monthly_income']        num_children = request.form['num_children']        is_urban = request.form['is_urban']                if not all(request.form.values()):            error_message = "Please fill all the fields."            return render_template('index.html', error=error_message)        model = pickle.load(open('model.pkl', 'rb'))        test_vector = [num_rooms, num_people, house_area, is_ac, is_tv, is_flat, avg_monthly_income, num_children, is_urban]        test_vector = np.array(test_vector).reshape(1, -1)        test_vector = test_vector.astype(float)        pred_val = model.predict(test_vector)        amt = pred_val[0]            return render_template("predictor.html", amount = amt)if __name__ == "__main__":    app.run(port=8346, debug=True)    